import type { Base58String, E164Phone, SocialAuthType } from '@particle-network/auth-core';
import type { Chain } from '@particle-network/chains';
export type EthereumAuthorization = {
    uniq: boolean;
};
export type SolanaAuthorization = {
    message: Base58String;
} & EthereumAuthorization;
export type Authorization = EthereumAuthorization | SolanaAuthorization;
export type LoginAuthorization = {
    authorization: Authorization;
    chain: Chain;
};
export type ConnectWithSocialOptions = {
    socialType: SocialAuthType;
    code?: string;
    nonce?: string;
    prompt?: 'none' | 'consent' | 'select_account';
};
export type ConnectWithJWTOptions = {
    jwt: string;
};
export type ConnectWithPhoneParam = {
    phone: E164Phone;
    code?: string;
};
export type ConnectWithEmailParam = {
    email?: string;
    code?: string;
};
export type ConnectOptions = (ConnectWithPhoneParam | ConnectWithEmailParam | ConnectWithSocialOptions | ConnectWithJWTOptions) & {
    authorization?: Authorization;
    chain?: Chain;
};
